import guid from 'guid';

export default class Node {

  #identity = {
    id: guid(),

    name: guid(),
    version: 0,
  };

  #content = {
  }

  constructor({name}){
    this.#identity.name = name;
  }

  get name(){ return this.#identity.name; }
  set name(name){ this.#identity.name = name; }

}
